mapper.update:
UPDATE setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

mapper.select:
SELECT setmeal.*,category.name categoryName
FROM setmeal LEFT JOIN category
    ON setmeal.category_id = category.id


        setmeal.name LIKE CONCAT('%',#{name},'%')


        AND setmeal.status = #{status}


        AND setmeal.category_id = #{categoryId}

select * from setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}
